n\\t\\tRemoteEvents.ReportHitbox:FireServer(MoveType, Victim, Victim.HumanoidRootPart.CFrame)\\n\\tend\\n\\t\\n\\tHitbox:GetPartBoundsInBox(Character, HitboxData, true, SendInfo)\\nend)\\n  \\n \\n \\n \\nServer does safety checks \\n  RemoteEvents.ReportHitbox.OnServerEvent:Connect(function(Player, MoveType : string, Victim : Instance, HitPosition : CFrame)\\n\\tif (typeof(Player) and typeof(Victim)) ~= \\"Instance\\" and typeof(HitPosition) ~= \\"CFrame\\" then return end\\n\\tlocal Character = Player.Character\\n\\t\\n\\tif Victim == Character then return end\\n\\tlocal MoveData = StateManager:GetMetaData(Character).MoveData\\n\\tlocal ServerHitboxCF = Character.HumanoidRootPart.CFrame * MoveData[MoveType].HitboxData.CF\\n\\t\\n\\tif os.clock() - MoveData[MoveType].StartTime &lt;= MoveData[MoveType].HitboxData.Duration then\\n\\t\\tif (HitPosition.Position - ServerHitboxCF.Position).Magnitude &lt;= 6 then\\n\\t\\t\\tMoveData[MoveType] =", "upvotes": 1, "category": "scripting-support", "comments": 3, "created_at": "2026-01-04T15:21:15.896Z"}
