mber3()\\n\\tthrottle = 0.5\\n\\tsteer = 0\\n\\tprint(\\"3\\")\\nend\\n\\nlocal function number4()\\n\\tthrottle = 0.55\\n\\tsteer = 0\\n\\tprint(\\"4\\")\\n\\nend\\n\\nlocal function number5()\\n\\tthrottle = 0.6\\n\\tsteer = 0\\n\\tprint(\\"5\\")\\nend\\n\\nlocal function number6()\\n\\tthrottle = 0.65\\n\\tsteer = TURN_STEER\\n\\tprint(\\"6\\")\\nend\\n\\nlocal function number7()\\n\\tthrottle = 0.7\\n\\tsteer = 0\\n\\tprint(\\"7\\")\\nend\\n\\nlocal function number8()\\n\\tthrottle = 0.75\\n\\tsteer = 0\\n\\tprint(\\"8\\")\\nend\\n\\nlocal function number9()\\n\\tthrottle = 0.8\\n\\tsteer = 0\\n\\tprint(\\"9\\")\\nend\\n\\nlocal function number10()\\n\\tthrottle = 0.9\\n\\tsteer = 0\\n\\tprint(\\"10\\")\\nend\\n\\nlocal function number11()\\n\\tthrottle = 1\\n\\tsteer = 0\\n\\tprint(\\"11\\")\\nend\\n\\nlocal numberActions = {\\n\\t[\\"Start\\"] = startAction,\\n\\t[1] = number1,\\n\\t[2] = number2,\\n\\t[3] = number3,\\n\\t[4] = number4,\\n\\t[5] = number5,\\n\\t[6] = number6,\\n\\t[7] = number7,\\n\\t[8] = number8,\\n\\t[9] = number9,\\n\\t[10] = number10,\\n\\t[11] = number11\\n}\\n\\nRunService.Heartbeat:Connect(function()\\n\\tif not active or not canMove then\\n\\t\\tseat.ThrottleFloat = 0\\n\\t\\tseat.SteerFloat = 0\\n\\t\\treturn\\n\\tend\\n\\n\\tif math.abs(steer) &lt; 0.01 then\\n\\t\\tseat.SteerFloat = 0\\n\\telse\\n\\t\\tseat.SteerFloat = steer\\n\\ten", "upvotes": 0, "category": "scripting-support", "comments": 4, "created_at": "2026-01-03T09:37:55.470Z"}
