ction disableMovement()\\n\\tcontrols:Disable()\\nend\\n\\nlocal function enableMovement()\\n\\tcontrols:Enable()\\nend\\n\\nlocal function disablePlayerCamera()\\n\\tif Camera then\\n\\t\\tCamera.CameraType = Enum.CameraType.Scriptable\\n\\telse\\n\\t\\twarn(\\"Camera not found!\\")\\n\\t\\treturn\\n\\tend\\nend\\n\\nlocal function enablePlayerCamera()\\n\\tif Camera then\\n\\t\\tCamera.CameraType = Enum.CameraType.Fixed\\n\\telse\\n\\t\\twarn(\\"Camera not found!\\")\\n\\t\\treturn\\n\\tend\\nend\\nif Camera then\\n\\tCamera.CameraType = Enum.CameraType.Scriptable\\nelse\\n\\twarn(\\"Camera not found!\\")\\n\\treturn\\nend\\ndisableMovement()\\n\\nlocal function tweenToCamera(cameraPart, duration)\\n\\tif cameraPart and cameraPart:IsA(\\"BasePart\\") then\\n\\t\\tlocal tweenInfo = TweenInfo.new(duration, Enum.EasingStyle.Linear)\\n\\t\\tlocal goal = { CFrame = cameraPart.CFrame }\\n\\t\\tlocal tween = TweenService:Create(Camera, tweenInfo, goal)\\n\\t\\ttween:Play()\\n\\t\\ttween.Completed:Wait()\\n\\tend\\nend\\n  ", "upvotes": 0, "category": "scripting-support", "comments": 3, "created_at": "2025-12-27T20:56:47.758Z"}
