 rawImpulse.Unit * maxImpulse\\n\\tend\\n\\n\\tprint(rawImpulse.Magnitude)\\n\\n\\thrp:ApplyImpulse(rawImpulse)\\n\\n\\ttask.delay(IMPULSE_TIME, function()\\n\\t\\tif hrp then\\n\\t\\t\\thrp:SetNetworkOwner(nil)\\n\\t\\tend\\n\\tend)\\n\\nend)\\n\\n  \\n Client: \\n  local PUSH_RANGE = 2.8\\nlocal COOLDOWN = 0.5\\nlocal IMPULSE_FACTOR = 50\\n\\nlocal lastPushTimes = {}\\n\\nRunService.RenderStepped:Connect(function()\\n\\tlocal vel = hrp.AssemblyLinearVelocity\\n\\t\\n\\tfor _, enemy in ipairs(Players:GetPlayers()) do\\n\\t\\tif enemy ~= plr then\\n\\t\\t\\tlocal char = enemy.Character\\n\\t\\t\\tif char and enemy:FindFirstChild(\\"you_turn\\") and not enemy.you_turn.Value then\\n\\t\\t\\t\\tlocal enemyHRP = char:FindFirstChild(\\"HumanoidRootPart\\")\\n\\t\\t\\t\\tif enemyHRP then\\n\\t\\t\\t\\t\\tlocal offset = enemyHRP.Position - hrp.Position\\n\\t\\t\\t\\t\\tlocal distance = offset.Magnitude\\n\\t\\t\\t\\t\\t\\n\\t\\t\\t\\t\\tif distance &gt; 0 and distance &lt;= PUSH_RANGE then\\n\\t\\t\\t\\t\\t\\tlocal lastPush = lastPushTimes[enemy] or 0\\n\\t\\t\\t\\t\\t\\tif os.clock() - lastPush &gt;= COOLDOWN then\\n\\t\\t\\t\\t\\t\\t\\tlocal dir = Vector3.new(offset.X, 0, offset.Z).Unit\\n\\t\\t\\t\\t\\t\\t\\tlocal impulse = dir * vel.Magnitude * IMPUL", "upvotes": 0, "category": "scripting-support", "comments": 3, "created_at": "2025-12-25T20:52:39.709Z"}
